import React, { useEffect, useState } from 'react'
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';



const generateOptions = (data) => {

  return {
    chart: {
      height: 500,
    },
    title: {
      text: 'Tổng ca nhiễm',
    },
  
    colors: ['#F3585B'],
    
    series: [
      {
        name: 'Tổng Ca nhiễm',
        data: generateOptions.Confirmed,
      },
    ],
  };
};

export default function Echarts({ data, report }) {
  const [options, setOptions] = useState({});
  const [reportType, setReportType] = useState('all');
  useEffect(()=>{
    setOptions(generateOptions(data));
  }, [data]);

  console.log('lightcharts',{ data });

  useEffect(() => {
    let customData = [];
    switch (reportType) {
      case 'all':
        customData = data;
        break;
      case '30':
        customData = data.slice(Math.max(data.length - 30, 1));
        break;
      case '7':
        customData = data.slice(Math.max(data.length - 7, 1));
        break;

      default:
        customData = data;
        break;
    }

    setOptions(generateOptions(customData));
  }, [data, reportType]);



  return (
    <div className='Echarts'>
      <div className='chart__statistical'>
        <div className='chart__infections'>
          <HighchartsReact
            data={report}
            highcharts={Highcharts}
            options={options}

          />
        </div>
        <div className='chart__deaths'>
        </div>
      </div>
      <div className='chart__map'>

      </div>

    </div>
  )
}
