import React, { useEffect, useState } from 'react'
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';





export default function Echarts({ data, report }) {
 
  const generateOptions = (report) => {

    return {
      chart: {
        height: 500,
      },
      title: {
        text: 'Tổng ca nhiễm',
      },
    
      colors: ['#F3585B'],
      
      series: [
        {
          name: 'Tổng Ca nhiễm',
          data : report
        },
      ],
    };
  };
  const [options, setOptions] = useState({});

  useEffect(()=>{
    setOptions(generateOptions(report));
  }, [report]);

  console.log('lightcharts',{ report });
  return (
    <div className='Echarts'>
      <div className='chart__statistical'>
        <div className='chart__infections'>
         
        </div>
        <div className='chart__deaths'>
        </div>
      </div>
      <div className='chart__map'>

      </div>

    </div>
  )
}
