import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';



const generateOptions = (data) => {

  return {
    chart: {
      height: 500,
    },
    title: {
      text: 'Tổng ca nhiễm',
    },
  
    colors: ['#F3585B'],
    
    series: [
      {
        name: 'Tổng Ca nhiễm',
        data: item.Confirmed,
      },
    ],
  };
};

export default function Echarts({ data, report }) {
  const [options, setOptions] = useState({});

  useEffect(()=>{
    setOptions(generateOptions(data));
  }, [data]);

  console.log('lightcharts',{ data });
  return (
    <div className='Echarts'>
      <div className='chart__statistical'>
        <div className='chart__infections'>
          <HighchartsReact
            data={report}
            highcharts={Highcharts}
            options={options}

          />
        </div>
        <div className='chart__deaths'>
        </div>
      </div>
      <div className='chart__map'>

      </div>

    </div>
  )
}
