import React, { useEffect, useState } from 'react'
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';





export default function Echarts({ report }) {
 
  const generateOptions = (report) => {

    return {
      chart: {
        height: 400,
      },
      title: {
        text: 'Tổng ca nhiễm',
      },
    
      colors: ['#F3585B'],
      
      series: [
        {
          name: 'Tổng Ca nhiễm',
          data : report.map((item) => item.Deaths),
          
        },
      ],
      series: [
        {
          name: 'Tổng Ca nhiễm',
          data : report.map((item) => item.Deaths),
          
        },
      ],
    };
  };
  const [options, setOptions] = useState({});

  useEffect(()=>{
    setOptions(generateOptions(report));
  }, [report]);

  console.log('lightcharts',{ report });
  return (
    <div className='Echarts'>
      <div className='chart__statistical'>
        <div className='chart__restores'>
          <HighchartsReact
            data={report}
            highcharts={Highcharts}
            options={options}
          />
        </div>
        <div className='chart__deaths'>
        </div>
      </div>
      

    </div>
  )
}
