import React, { useEffect, useState } from 'react';
import { sortBy } from 'lodash';
import { getCountries } from './Element/API';
import Headtitle from './Element/Headtitle'
import Model3D from './Element/Model3D'
import Echarts from './Element/Echarts'
import Test from './Element/test';



function App() {
  const [countries, setCountries] = React.useState([]);
  const [selectedCountryId, setSelectedCountryId] = React.useState('');
  const [report, setReport] = React.useState([]);

  useEffect(() => {
    getCountries().then((res) => {
      const { data } = res;
      const countries = sortBy(data, 'Country');
      setCountries(countries);
      setSelectedCountryId('vn');

    });
  }, []);
  const handleOnChange = React.useCallback((e) => {
    setSelectedCountryId(e.target.value);

  }, []);
  console.log('tiel',countries);
  return (
    <>
      <div className="Header">
        <h1>
          Coronavirus (COVID-19)
        </h1>
      </div>
      <div className='choose__box'>
        <Headtitle />
        <Test countries={countries} value={selectedCountryId} handleOnChange={handleOnChange} />
      </div>
      <Model3D  countries={countries} value={selectedCountryId} handleOnChange={handleOnChange}/>
      <Echarts />
    </>
  );
}

export default App;
